#!/usr/bin/env node
"use strict"
var mcping = require('minecraft-ping');
var prt = parseInt(process.argv[3]) || 25565

if(!process.argv[2]){
  console.log("mcping <host> [port]")
  process.exit(64)
}

mcping.ping_fe01fa({host: process.argv[2], port: prt}, (err, res) => {
  if(!err){
    console.log(res.gameVersion+" ("+res.playersOnline+"/"+res.maxPlayers+")");
  }else{
    console.log("Closed Server.")
    process.exit(1)
  }
})
